import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import SiteIcon from "./SiteIcon"
import axios from "axios";

function Home(props) {
  const [link,setLink] = useState("");


// function handleClick(e)
// {
//    axios.post("/api/fetch", {
//       name: "Rahul Soni",
//       roll: "20NA10030"
//    })
//    .then(res=>
//     {
//       console.log(res.data)
//     })
// }


const submit = async(e)=>
{
   e.preventDefault()

   try{
    await axios.post("http://localhost:8000/",{
      link
    })
   }
   catch(e){
    console.log(e);
   }
}
 



  return (
    <React.StrictMode>
    <SiteIcon/>
      <div className="container homebox">
        <h1 className="heading">SEARCH FOR THE PRODUCTS</h1>
          
       <input className="form-control inputText" type="text" placeholder="Paste your producthunt link here." onChange={e=>(setLink(e.value))} value={link}/>
        <br />
        <input className="inputCheck" type="checkbox" id="suggestion" name="suggestion" />

        <span className = "inputCheck"> View suggestion</span>
        <br />
        <button className="btn btn-info" type="submit" onClick={e=>{submit && props.func(false)}}> view Details</button>
      

      </div>
    </React.StrictMode>
  );
}
export default Home;
